mapper.select:
SELECT SEQ , TITLE , CONTENT , GEN_DATE , UPT_DATE , CATEGORY FROM ( SELECT /*+ INDEX_DESC(FAQ IDX_FAQ) */ ROWNUM RN, SEQ, TITLE, CONTENT, GEN_DATE, UPT_DATE, CATEGORY FROM FAQ
AND ROWNUM <= #{pageNum} * #{amount}
) WHERE RN > (#{pageNum} - 1) * #{amount} ORDER BY SEQ DESC

mapper.insert:

	SELECT SEQ_FAQ.NEXTVAL FROM DUAL

INSERT INTO FAQ ( SEQ
				, TITLE
				, CONTENT
				, CATEGORY)
VALUES ( #{seq}
	   , #{title}
	   , #{content}
	   , #{category})

mapper.update:
UPDATE FAQ
SET TITLE = #{title}
  , CONTENT = #{content}
  , UPT_DATE = SYSDATE
  , CATEGORY = #{category}
WHERE SEQ = #{seq}
