					  , ORG_NAME
					  , FILE_TYPE
					  , FILE_SIZE
					  , GEN_DATE
					  , UPT_DATE
					  , NOTICE_SEQ)
VALUES ( #{seq}
	   , #{fileNo}
	   , #{name}
	   , #{orgName}
	   , #{fileType}
	   , #{fileSize}
	   , SYSDATE
	   , SYSDATE
	   , #{noticeSeq})
