mapper.select:
SELECT SEQ , TITLE , CONTENT , WRITER , VIEWS , GEN_DATE , UPT_DATE FROM ( SELECT /*+ INDEX_DESC(NOTICE IDX_NOTICE) */ ROWNUM RN, SEQ, TITLE, CONTENT, WRITER, VIEWS, GEN_DATE, UPT_DATE FROM NOTICE
AND ROWNUM <= #{pageNum} * #{amount}
) WHERE RN > (#{pageNum} - 1) * #{amount} ORDER BY SEQ DESC

mapper.insert:

	SELECT SEQ_NOTICE.NEXTVAL FROM DUAL

INSERT INTO NOTICE ( SEQ
				   , TITLE
				   , CONTENT
				   , WRITER)
VALUES ( #{seq}
	   , #{title}
	   , #{content}
	   , #{writer})

mapper.update:
UPDATE NOTICE
SET TITLE = #{title}
  , CONTENT = #{content}
  , WRITER = #{writer}
WHERE SEQ = #{seq}

UPDATE NOTICE
SET VIEWS = VIEWS + 1
WHERE SEQ = #{seq}
