mapper.insert:

	SELECT SEQ_QNA_ANSWER.NEXTVAL FROM DUAL

INSERT INTO QNA_ANSWER ( SEQ
					   , QNA_SEQ
					   , TITLE
					   , CONTENT
					   , WRITER)
VALUES ( #{seq}
	   , #{qnaSeq}
	   , #{title}
	   , #{content}
	   , #{writer} )

mapper.update:
UPDATE QNA_ANSWER
SET TITLE = #{title}
  , CONTENT = #{content}
  , UPT_DATE = SYSDATE
WHERE SEQ = #{seq}
